mapper.select:

 SELECT sellImageNo,sellImages
FROM PRODUCTSELLIMAGE
WHERE sellNo=#{sellNo}

mapper.update:

 			UPDATE PRODUCTSELL 
			SET GOODSGRADE = #{grade},startTime=TRUNC(SYSDATE, 'HH') ,endTime=TRUNC(SYSDATE, 'HH')+#{vailDate}
			WHERE SELLNO = #{sellNo}

mapper.select:

 		SELECT MAX(desiredPurPrice)
		FROM productPurchase
		WHERE SELLNO=#{sellNo}


 		SELECT MAX(desiredSellPrice) 
		FROM productsell
		WHERE ITEMSIZE=#{goodsSize} AND SELLNO =#{sellNo}

SELECT MAX(purchaseNo) AS purchaseNo, #{sellNo} AS sellNo , #{id} AS id
FROM productPurchase
WHERE sellNo = #{sellNo}


 		SELECT *
		FROM productsell
		WHERE itemSize=#{itemSize} and goods=#{goods}
